step-type.post-processing:
        commandOut.print("==== Starting post processing ====\n");
		properties.put("Status", "Success");

		scanner.register("box.auth.token:", function(lineNumber, line){
		    var auth_token = line.replace("box.auth.token:","");
		    commandOut.print(auth_token);
		    properties.put("box.auth.token",auth_token);
		});

		scanner.scan();

		var exit = properties.get('exitCode');

		if (exit == 0) {
		    properties.put('Status', 'Success');
		}
		else {
		     properties.put('Status', 'Failure');
		}
		commandOut.print("\n==== End post processing ====\n");

if (properties.get("exitCode") != 0) {
    properties.put(new java.lang.String("Status"), new java.lang.String("Failure"));
}
else {
    properties.put("Status", "Success");
}

